                        vDkm(m,t)=0;
                        Dmes(m,t)=0;
                        
                        % if sumDnme+vDkm-vDmk-Dmes>0 set Dmer(m,t)=0
                        if   (sumDnme(m,t)+vDkm(m,t)-vDmk(m,t)<=0)
                            Dmer(m,t)=0;
                        end
                        
                        if (sumDnme(m,t)+vDkm(m,t)-vDmk(m,t)-Dmes(m,t)>0)
                            Dmer(m,t)=sumDnme(m,t)+vDkm(m,t)-vDmk(m,t)-Dmes(m,t);
                        end
                        
                    T(m,t)=max(sumDnme(m,t)+vDkm(m,t)-vDmk(m,t)-Dmes(m,t)-Dmer(m,t),0);   % Problem 1: when t=1, Dmer(m,1)=672; while others equals 0; fixed by Long Chen
                    end
              end
                   % put the code here to determine the real allocation
                   % between edge servers on the incoming and outcoming
                   % computation tasks.
                   % t>=2
                    
               
                    
        t=t+1;   % print  the time slot
        
       
             
        for n=1:N
         Q(n,t)=max(Q(n,t-1)-Dnl(n,t-1)-Dnme(n,t-1)-Dnr(n,t-1),0)+A(n,t-1);   % update the queue of the mobile devices
        end     
        
           [Dnme_t]=cal_Dnme(Loc,omega,beta_n,tau,Gamma_nm,Ptxnstar,N0,t); % some bug exists, bug fixed by lonchen@mail.ustc.edu.cn at 2018-12-24
              for n=1:N
                     Dnme(n,t)=Dnme_t(n,1);
              end
                  [s_Dnme] = cal_sumDnme(Loc,Dnme,T,t);  
              for m=1:M
              sumDnme(m,t)=s_Dnme(m,1);    % assign the value
              end
              
                  [f_nstar_t]=cal_opt_f_n(N,Q,tau,Wn,V,wn,kappa,f_nmax,t);                           % Call the function of optimal CPU-cycle frequency of mobile devices
            for n=1:N
            f_nstar(n,t)=f_nstar_t(n,1);
            end
            for n=1:N
                Dnl(n,t)=tau*f_nstar(n,t)/Wn;               
                pnl(n,t)=kappa*f_nstar(n,t)^(3);
            end
            % Calculate the optimal transmission power of  mobile devices 
            Ptxnstar=calc_Ptxnstar(wn,N0,P_nmax, Gamma_nm, Gamma_nr,Q,beta_n,omega,tau,V,N,t);
            for n=1:N
                Dnr(n,t)=omega*beta_n(n,1)*tau*log2(1+( Gamma_nr(n,t)*Ptxnstar(n,t) )/(N0*omega*beta_n(n,1) ));
                % for the cloud computing case, there is no need to limit
                % the transmission rate by times 0.1 in this case, I should
                % add
            end
            
               for m=1:M
                 
                 Zme(m,t)=0; % update the virtual queue.
                 
              end
           
              [ f_emstar_t]=cal_femstar(f_emmax,Zme,delta,tau,Lm,V,wnm,kappae,t ); % Call the function of the optimal CPU-cycle frequency of the edge server
             for m=1:M
              f_emstar(m,t)=f_emstar_t(m,1);
             end
              for m=1:M
                  Dmes(m,t)=tau*f_emstar(m,t)*Lm^(-1);     % Calcuate the D_m^{es}(t) given the optimal frequency of edge server
              end
            
           
                    %%
                           [sortedT,IndT]=sort(T(:,t-1),'ascend'); % sortedT is the result, IndT is the original index in array vT
                   for m=1:M
                       vDmk(1:M,t)=exprnd(delta*Dmes(m,t),M,1);  % set as the maximum available tasks although actully, vDmk may be lower                  
                       % The second constraint in the subproblem
                       % $\mathcal{SP}5$ has been met
                       %  Next deal with the first constraint, to see
                       %  whether the constraint is met or not
                       [sortedvDmk, IndvDmk]=sort(vDmk(:,t),'descend'); % obtain the maximum size of data that intends to move to neighbor
                       % IndvDmk src     Transfer       IndT dst
                       %   3    Large     --->  1  less load
                       %   1    Middle  --->  2  Middle load
                       %   2    Small   ---->  3  Heavy load
                       for i=1:length(IndT)                                                
                                % temperaly store the sum value of vDkm  do not change with time (should have m temp values, bug found)
                               vDkm(IndT(i),t)= sortedvDmk(i);                                                                       
                                  if (  vDkm(IndT(i),t)<= f_emstar(IndT(i),t)*tau/Wn-sumDnme(IndT(i),t))
                                   % Constraint one satisfied, start true assignment
                                   T(m,t)=max( T(m,t-1)-Dmes(m,t-1)-vDmk(m,t-1)-Dmer(m,t-1),0)+  vDkm(IndT(i),t) +sumDnme(m,t-1);
                                  end %  end if             
                       end % end for
                           %The next, we should update the queues on the edge
                       %server themselves.
                               
                   end % end for              
                    %%                        
             f_rstar_t=cal_frstar(f_rmax,Dnr,Dmer,tau,Wn,t);  % Call the function of the optimal CPU-cycle frequency of the remote cloud server at time slot t
             f_rstar(t,1)=f_rstar_t;
             Phi(t,1)=tau*f_rstar(t,1)*Ls^(-1); % 
           
        G(t,1)=max(G(t-1,1)-Phi(t-1,1),0)+sum(Dnr(:,t-1))+sum(Dmer(:,t-1)); % update the queue of the remote cloud server
              
        end %end while 
                    
end % end function jsac



    
