    function [s_Dnme] = cal_sumDnme(Loc,Dnme,T,t) %Dvmk,Dvkm,Dmes,f_emstar,Wn, Dmer
                    [M,t_max]=size(T);  % obtain the data size vector row M and column t_max
                    % size(Dnme)=(N,t_max)=6 1500   
                    % size(Dmer)=3   1500
                    % size(Dvmk)=size(Dvkm)=(M,M,t_max)=3 3 1500
                    % sumDnme is initialized at the beginning
               % initialize the $sum_{n=1}^{N} D_{nm}^{e}(t)$ for any $m\in \mathcal{M}$
                        s_Dnme=zeros(M,1);
                        for m=1:M
                            temp=0;
                            index_n_attatch=find(Loc(:,m,t)==1);
                            for ind_n=1:length(index_n_attatch)
                              temp=temp+Dnme(index_n_attatch(ind_n),t);
                            end
                           s_Dnme(m,1)=temp;   % store the sum value, the first version contains a bug where for each iteration, the sumDnme is set as 0
                        end
              
                        
                        % virtually allocation among edge servers           
    end
