% Calculate the optimal CPU-cycle frequency of the edge server
function [f_emstar_t]=cal_femstar(f_emmax,Zme,delta,tau,Lm,V,wnm,kappae,t )
   %  t_max=size(Zme,2);
     M=size(Zme,1);
     f_emstar_t=zeros(M,1);
         for m=1:M
         f_emstar_t(m,1)=min(f_emmax,max(sqrt( (Zme(m,t)*delta*tau*Lm^(-1))/(3*V*wnm*kappae) ) ,0));
         end 
end
