% Calculate the optimal CPU-cycle frequency of the remote cloud server
function [f_rstar_t]=cal_frstar(f_rmax,Dnr,Dmer,tau,Wn,t)
t_max=length(Dnr);
    f_rstar_t=zeros(1,1);
    if t==1
        f_rstar_t=0;
   
    else
             f_rstar_t=min(f_rmax, (sum(Dnr(:,t-1))+sum(Dmer(:,t-1)))*Wn/(tau));
    end
end
