% Calculate the optimal transmission power of the mobile devices
function [Ptxnstar,H, I, J]=calc_Ptxnstar(wn,N0,P_nmax, Gamma_nm, Gamma_nr,Q,beta_n,omega,tau,V,N,t)
 t_max=length(Gamma_nm);
Ptxnstar=ones(N,t_max)*0.5; % initialize the parameter
H=zeros(N,t_max); % initialize the parameter
I=zeros(N,t_max); % initialize the parameter
J=zeros(N,t_max); % initialize the parameter
     for i=1:N           
               H(i,t)=Gamma_nm(i,t)*Gamma_nr(i,t)*V*wn*(log(2)/log(exp(1)));
               I(i,t)=V*wn*(log(2)/log(exp(1)))*[N0*omega*beta_n(i)*Gamma_nm(i,t)+N0*omega*beta_n(i)*Gamma_nr(i,t)]-2*Q(i,t)*omega*beta_n(i)*tau*Gamma_nm(i,t)*Gamma_nr(i,t);
               J(i,t)=V*wn*(log(2)/log(exp(1)))*[N0^2*omega^2*beta_n(i)^2]-Q(i,t)*[N0*omega^2*beta_n(i)^2*tau*Gamma_nm(i,t)+N0*omega^2*beta_n(i)^2*tau*Gamma_nr(i,t)];
               Ptxnstar(i,t)=min(P_nmax, max((-I(i,t)+sqrt(I(i,t)^2-4*H(i,t)*J(i,t)))/(2*H(i,t)),0));        
     end
end
