% Calculate the function $\gamma_{n,m}$ at time slot t
% Calculate the function $\gamma_{n,r}$ at time slot t
   function [Gamma_nm]=cal_Gammanm(gamma_nm,N,t_max,g0,d0,alpha,dnm)
       Gamma_nm=zeros(N,t_max);
       for n=1:N
           for t=1:t_max
               Gamma_nm(n,t)=gamma_nm(n,t)*g0*10^(3)*(d0/dnm(n,t))^(alpha);
           end
       end
   end

    function [Gamma_nr]=cal_Gammanr(gamma_nr,N,t_max,g0,d0,alpha,dnr)
       Gamma_nr=zeros(N,t_max);
       for n=1:N
           for t=1:t_max
               Gamma_nr(n,t)=gamma_nr(n,t)*g0*10^(2)*(d0/dnr(n,t))^(alpha);
           end
       end
   end
