% Calculate the optimal CPU-cycle frequency of the mobile devices
function [f_nstar_t]=cal_opt_f_n(N,Q,tau,Wn,V,wn,kappa,f_nmax,t)
      t_max=length(Q);
      f_nstar_t=zeros(N,1);  % initialize the optimal frequency of the mobile device $n$ at time slot t.
      for n=1:N   % N=6 it's OK.
              f_nstar_t(n,1)=min(f_nmax, sqrt((Q(n,t)*tau)./(3*V*wn*kappa*Wn )));   % calculat the optimal CPU-cycle frequency according to the equation (40)
      end
end
